// JavaScript code to calculate first day of current month
const now = new Date();
const firstDayOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);
const firstDayStr = firstDayOfMonth.toISOString().substring(0, 10);
return [{ json: { firstDayOfMonth: firstDayStr } }];

// Initialize an array to collect all processed values
let allMessages = [];

// Loop over all input items
for (const item of $input.all()) {
  let val = item.json.my_field_1 || ""; // Use current item's field

  // Replace 4 spaces with newline
  val = val.replace(/ {4}/g, "\n");

  // Add to our collection
  allMessages.push(val);
}

// Join all messages with double newlines for separation
const combinedMessage = allMessages.join("\n\n");

// Create a SINGLE new item with the combined message
return [{
  json: {
    myNewField: combinedMessage,
    totalItemsCombined: allMessages.length,
    originalItems: $input.all().map(item => item.json) // Optional: keep original data
  }
}];

// Get all items
const items = $input.all();
let combinedValue = "";

// Loop over all items and combine their processed values
for (const item of items) {
  // FIXED: Access the current item's data, not from previous node
  let val = item.json.my_field_1 || "";
  val = val.replace(/ {4}/g, "\n");

  // Add separator if not the first item
  if (combinedValue) {
    combinedValue += "\n\n";
  }
  combinedValue += val;
}

// Add the combined message to the first item
items[0].json.myNewField = combinedValue;
items[0].json.combinedFromTotalItems = items.length;

return items;

